import React, { Fragment, useEffect } from "react";
import { DataGrid } from "@material-ui/data-grid";
import "./productList.css";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { useAlert } from "react-alert";
import { Button } from "@material-ui/core";
import MetaData from "../layout/MetatData";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import SideBar from "./Sidebar";
import {
  deleteOrder,
  getAllOrders,
  clearErrors,
} from "../../actions/orderAction";
import { DELETE_ORDER_RESET } from "../../constants/orderConstants";
import { Navigate, useNavigate } from "react-router-dom";

const OrderList = ({ history }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const alert = useAlert();

  const { error, orders } = useSelector((state) => state.allOrders);

  const { error: deleteError, isDeleted } = useSelector((state) => state.order);

  const deleteOrderHandler = (id) => {
    dispatch(deleteOrder(id));
  };

  useEffect(() => {
    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

    if (deleteError) {
      alert.error(deleteError);
      dispatch(clearErrors());
    }

    if (isDeleted) {
      alert.success("Order Deleted Successfully");
      navigate("/admin/orders");
      dispatch({ type: DELETE_ORDER_RESET });
    }

    dispatch(getAllOrders());
  }, [dispatch, alert, error, deleteError, history, isDeleted]);

  const columns = [
    { field: "id", headerName: "Order ID", minWidth: 300, flex: 1 },

    {
      field: "status",
      headerName: "Status",
      minWidth: 150,
      flex: 0.5,
      cellClassName: (params) => {
        return params.getValue(params.id, "status") === "Delivered"
          ? "greenColor"
          : "redColor";
      },
    },
    {
      field: "itemsQty",
      headerName: "Items Qty",
      type: "number",
      minWidth: 150,
      flex: 0.4,
    },

    {
      field: "amount",
      headerName: "Amount",
      type: "number",
      minWidth: 270,
      flex: 0.5,
    },

    {
      field: "actions",
      flex: 0.3,
      headerName: "Actions",
      minWidth: 150,
      type: "number",
      sortable: false,
      renderCell: (orderId) => {
        return (
          <Fragment>
            <Link to={`/admin/order/${orderId}`}>
              <EditIcon />
            </Link>

            <Button
              onClick={() =>
                deleteOrderHandler(orderId)
              }
            >
              <DeleteIcon />
            </Button>
          </Fragment>
        );
      },
    },
  ];

  const rows = [];

  orders &&
    orders.forEach((item) => {
      rows.push({
        id: item._id,
        itemsQty: item.orderItems.length,
        amount: item.totalPrice,
        status: item.orderStatus,
      });
    });

  return (
    <Fragment>
      <MetaData title={`ALL ORDERS - Admin`} />

      <div className="dashboard">
        <SideBar />
        <div className="productListContainer">
          <h1 id="productListHeading">ALL ORDERS</h1>

          <table className="productListTable">
            <thead>
              <tr> 
                {
                  columns.map(column => (
                    <th key={column.field}>{column.headerName}</th>
                  ))  
                } 
              </tr>
            </thead>   

            <tbody>   
            {
              rows.map(row => {  
                return (      
                  <tr key={row.id}>       
                    {
                      columns.map(column => {      
                        const value = row[column.field]; 

                        if (column.field === 'actions') {
                          return <td key='actions'>{column.renderCell(row.id)}</td> 
                        }
                        const className =
                          column.field === "status"
                            ? row[column.field] === "Delivered"
                              ? "greenColor"
                              : row[column.field] === "Processing"
                              ? "yellowColor"
                              : "redColor"
                            : "";

                            const style = {
                              color: className === "yellowColor" ? "green" : className === "greenColor" ? "red" : "inherit",
                              fontWeight: className === "yellowColor" ? "bold" : className === "greenColor" ? "bold" : "normal",
                            };

                        return <td key={column.field} className={className} style={style}>{value.toLocaleString('en-US')}</td>  
                      })  
                    }
                  </tr>
                )    
              })  
            }
          </tbody>
          </table>
        </div>
      </div>
    </Fragment>
  );
};

export default OrderList;
