import React, { Fragment, useEffect } from "react";
import { DataGrid } from "@material-ui/data-grid";
import "./productList.css";
import { useSelector, useDispatch } from "react-redux";
import { clearErrors, getAdminProduct,
  deleteProduct } from "../../actions/productAction";
import { Link } from "react-router-dom";
import { useAlert } from "react-alert";
import { Button } from "@material-ui/core";
import MetaData from "../layout/MetatData";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import SideBar from "./Sidebar";
import { DELETE_PRODUCT_RESET } from "../../constants/productConstants";

const ProductList = ({ history }) => {
  const dispatch = useDispatch();

  const alert = useAlert();

  const { error, products } = useSelector((state) => state.products);

  const { error: deleteError, isDeleted } = useSelector(
    (state) => state.products
  );

  const deleteProductHandler = (id) => {
    dispatch(deleteProduct(id));
  };

  useEffect(() => {
    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

    if (deleteError) {
      alert.error(deleteError);
      dispatch(clearErrors());
    }

    if (isDeleted) {
      alert.success("Product Deleted Successfully");
      history.push("/admin/dashboard");
      dispatch({ type: DELETE_PRODUCT_RESET });
    }

    dispatch(getAdminProduct());

  }, [dispatch, alert, error, deleteError, history, isDeleted]);


  const columns = [
    { field: "id", headerName: "Product ID", minWidth: 200, flex: 0.5 },

    {
      field: "name",
      headerName: "Name",
      minWidth: 350,
      flex: 1,
    },
    {
      field: "stock",
      headerName: "Stock",
      type: "number",
      minWidth: 150,
      flex: 0.3,
    },

    {
      field: "price",
      headerName: "Price",
      type: "number",
      minWidth: 270,
      flex: 0.5,
    },

    {
      field: "actions",
      flex: 0.3,
      headerName: "Actions",
      minWidth: 150,
      type: "number",
      sortable: false,
      renderCell: (productID) => {
        return (
          <Fragment>
            <Link to={`/admin/product/${productID}`}>
              <EditIcon />
            </Link>

            <Button
              onClick={() =>
                deleteProductHandler(productID)
              }
            >
              <DeleteIcon />
            </Button>
          </Fragment>
        );
      },
    },
  ];

  const rows = [];

  products &&
    products.forEach((item) => {
      rows.push({
        id: item._id,
        stock: item.Stock,
        price: item.price,
        name: item.name,
      });
    });


    console.log(rows)
    console.log(products)

  return (
    <Fragment>
      <MetaData title={`ALL PRODUCTS - Admin`} />

      <div className="dashboard">
        <SideBar />
        <div className="productListContainer">
          <h1 id="productListHeading">ALL PRODUCTS</h1>

          <table className="productListTable">
            <thead>
              <tr>
                {columns.map((column) => (
                  <th key={column.field} style={{ minWidth: column.minWidth, flex: column.flex }}>
                    {column.headerName}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>          
  {rows.map((row) => (
     <tr key={row.id}>
       <td>{row.id}</td>
       <td>{row.name}</td>
       <td>{row.stock}</td>
       <td>{row.price.toLocaleString('en-US')}</td>
       <td>
         <Link to={`/admin/product/${row.id}`}>
           <EditIcon />
         </Link>
         <Button onClick={() => deleteProductHandler(row.id)}>
           <DeleteIcon />
         </Button>
       </td>         
     </tr>         
   ))}       
</tbody>
          </table>
        </div>
      </div>
    </Fragment>
  );
};

export default ProductList;
